import './App.css';
import React, { useState, useEffect } from 'react';
import { TbArrowBarDown } from 'react-icons/tb';
import ListItem from './components/ListItem';

function App() {
  const [todo, setTodo] = useState('');
  const [allTodos, setAllTodos] = useState([]);

  const addTodo = e => {
    e.preventDefault();

    const todoItem = {
      id: new Date().getTime(),
      text: todo,
      isChecked: false
    };

    if (todo !== '') {
      setAllTodos([...allTodos, todoItem]);
      setTodo('');
    }
  }

  useEffect(() => {
    localStorage.setItem('todo', JSON.stringify(allTodos));
  }, [])

  return (
    <div className='App'>
      <div className='App_todo'>
        <form className='App_input_wrapper' onSubmit={addTodo}>
          <input 
            type='text' 
            className='App_input' 
            value={todo}
            onChange={e => setTodo(e.target.value)} />
          <div className='App_input_button' onClick={addTodo}>
            <TbArrowBarDown size={24}/>
          </div>
        </form>

        <div className='App_todo_list'>
          <ListItem />
        </div>
      </div>
    </div>
  );
}

export default App;
