import './ListItem.css'
import { AiOutlineCheck } from 'react-icons/ai';
import { FaEdit } from 'react-icons/fa';
import { ImBin } from 'react-icons/im' ;


const ListItem = ({ text, deleteTodo, isChecked, toggleChecked, onEditTodo }) => {
    return (
        <div className="todo_list">
            <div className={isChecked ? 'todo_checked' : 'todo_unchecked'} onClick={toggleChecked}>
                {isChecked && <AiOutlineCheck />}
            </div>
            <div className={isChecked ? 'todo_text_checked' : 'todo_text'}>
                {text}
            </div>
            <div className='todo_edit'>
                <FaEdit />
            </div>
            <div className='todo_delete' onClick={() => deleteTodo()}>
                <ImBin />
            </div>
        </div>
    )
}

export default ListItem
